import FormularioAlta from './FormularioAlta'

const PRODUCTS = [
  {category: 'Sport Goods', price: '$49.99', stocked: true, name: 'Football'},
  {category: 'Sporting Goods', price: '$9.99', stocked: true, name: 'Baseball'},
  {category: 'Sporting Goods', price: '$29.99', stocked: false, name: 'Basketball'},
  {category: 'Electronics', price: '$99.99', stocked: true, name: 'iPod Touch'},
  {category: 'Electronics', price: '$399.99', stocked: false, name: 'iPhone 5'},
  {category: 'Electronics', price: '$199.99', stocked: true, name: 'Nexus 7'}
];

const PRODUCTS2 = [
  {id: '1222', nombre: 'Nike', estado: 'Proveedor online', stocked: 1},
  {id: '3232', nombre: 'Adidas', estado: 'Proveedor online', stocked: 1},
  {id: '3434', nombre: 'Puma', estado: 'Proveedor Menudeo', stocked: 0},
  {id: '4545', nombre: 'Pirma', estado: 'Proveedor Menudeo', stocked: 0},
  {id: '6766', nombre: 'Wilson', estado: 'Proveedor Mayoreo', stocked: 2},
  {id: '9655', nombre: 'Jordan', estado: 'Proveedor Mayoreo', stocked: 2}
];


function App() {

  const usuariosData = [
    {category: 'Sport Goods', price: '$49.99', name: 'Football'},
    {category: 'Sporting Goods', price: '$9.99',  name: 'Baseball'},
    {category: 'Sporting Goods', price: '$29.99',  name: 'Basketball'},
    {category: 'Electronics', price: '$99.99', name: 'iPod Touch'},
    {category: 'Electronics', price: '$399.99', name: 'iPhone 5'},
    {category: 'Electronics', price: '$199.99', name: 'Nexus 7'}
  ];



const [listaUsuarios, setListaUsuarios] = useState(usuariosData);

const addUsuario = (objetoUsuario)=>{
    setListaUsuarios([...listaUsuarios, objetoUsuario]);
}

  return (<div className='container'>
<FormularioAlta addUsuario={addUsuario} />
<div className="flex-row">
<div className="flex-large">

<div className='container'>
<h2>Filtro de deportes</h2>
<FilterableProductTable products={PRODUCTS}/>
</div>

</div>
  <div className="flex-large">
    <h2>Filtro de proveedores</h2>
    <FilterableProductTableP  products={PRODUCTS2}/>
    <br></br> <br></br>

  </div>
</div>
</div>

  );
}

export default App;
