import { useState } from "react"

const FormularioAlta = (props)=>{

      
    const usuariosData = [
        {category: 'Sport Goods', price: '$49.99', stocked: true, name: 'Football'},
        {category: 'Sporting Goods', price: '$9.99', stocked: true, name: 'Baseball'},
        {category: 'Sporting Goods', price: '$29.99', stocked: false, name: 'Basketball'},
        {category: 'Electronics', price: '$99.99', stocked: true, name: 'iPod Touch'},
        {category: 'Electronics', price: '$399.99', stocked: false, name: 'iPhone 5'},
        {category: 'Electronics', price: '$199.99', stocked: true, name: 'Nexus 7'}
      ];

    const estadoInicial ={category:'', price:'', name: 'Football'};
    const [usuario, setUsuario] = useState({estadoInicial});

    const handleInputChange = (event) => {
   
        setUsuario({
            ...usuario,
            [event.target.name] : event.target.value
        })
    }
    const GuardarUsuario = (event)=>{
    event.preventDefault()
       console.log(usuario)
       props.addUsuario(usuario);
    }
   
return(<section className="container">
<h1>Formulario Alta</h1>
    <form onSubmit={GuardarUsuario}>
    <input type="text" name="nombre" placeholder="Nombre" onChange={handleInputChange}/>
    <input type="text" name="usuario" placeholder="Usuario" onChange={handleInputChange}/>
    <button type="submit" >Enviar</button>
    </form>
  
</section>)
}

export default FormularioAlta
