import { useState } from "react"

const FormularioAlta = (props)=>{

      


    const estadoInicial ={id:'', category:'', price:'', name: ''};
    const [usuario, setUsuario] = useState(estadoInicial);

    const handleInputChange = (event) => {
        const {name, value} = event.target;
        setUsuario({...usuario,[name]: value})
        console.log(usuario)
        }
    
    

return(<section className="container">
<h1>Formulario Alta</h1>
    <form 
    onSubmit={event => {
        event.preventDefault();
        props.addUsuario(usuario)
        setUsuario(estadoInicial)
      }}>
    <input type="text" name="category" placeholder="category" onChange={handleInputChange} value={usuario.category}/>
    <input type="text" name="price" placeholder="price" onChange={handleInputChange} value={usuario.price} />
    <input type="text" name="name" placeholder="name" onChange={handleInputChange} value={usuario.name} />


    <button type="submit" >Enviar</button>
    </form>
  
</section>)
}

export default FormularioAlta
