import React, { useState, useEffect } from 'react'


const EditUserForm = (props) => {
  const [user, setUser] = useState(props.currentUser)


  useEffect(() => {
    setUser(props.currentUser)
  }, [props])

  const handleInputChange = (event) => {
    const { name, value } = event.target

    setUser({ ...user, [name]: value })
  }

  return (
    <form
      onSubmit={(event) => {
        event.preventDefault()

        props.updateUser(user.id, user)
      }}
    >
      <label>Nombre real</label>
      <input
        type="text"
        name="nombrereal"
        value={user.nombrereal}
        onChange={handleInputChange}
      />
      <label>Nombre de villano</label>
      <input
        type="text"
        name="nombredevillano"
        value={user.nombredevillano}
        onChange={handleInputChange}
      />
      <label>Edad</label>
      <input
        type="text"
        name="edad"
        value={user.edad}
        onChange={handleInputChange}
      />
      <label>Afiliacion</label>
      <input
        type="text"
        name="afiliacion"
        value={user.afiliacion}
        onChange={handleInputChange}
      />
      <label>Descrripcion</label>
    <textarea 
    name="descrripcion" 
    rows="10" 
    cols="50"
    value={user.descrripcion}
    onChange={handleInputChange}
    
    >Write something here</textarea>
      <button>Update user</button>
      <button
        onClick={() => props.setEditing(false)}
        className="button muted-button"
      >
        Cancel
      </button>
      
    </form>
  )
}

export default EditUserForm
