import React from 'react'
import { useState } from 'react'
import SearchBar from './SearchBar'
import ProductTable from './ProductTable'


const FilterableProductTable =(props)=> {
  const [filterText, setFilterText] = useState(""); 
  const [inStockOnly, setInStockOnly] = useState(0,2);
  const [inStockOnlyMayoreo, setInStockOnlyMayoreo] = useState(0,1);
  const [inStockOnlyMenudeo, setInStockOnlyMenudeo] = useState(1,2);



  return (
    <div >
      <h1>Proveedores</h1>
      <SearchBar 
        filterText={filterText}
        inStockOnly={inStockOnly}  

        onFilterTextChange={text => setFilterText(text)}
        onInStockChange={stock => setInStockOnly(stock)}





      />
      <ProductTable 
        products={props.products} 
        filterText={filterText}
        inStockOnly={inStockOnly} 
        inStockOnlyMayoreo={inStockOnlyMayoreo} 
        inStockOnlyMenudeo={inStockOnlyMenudeo} 


      />
    </div>
  );
}



export default FilterableProductTable
