import { useState } from "react"

const FormularioAlta = (props)=>{

      


    const estadoInicial ={id:'', category:'', price:'', name: ''};
    const [usuario, setUsuario] = useState({estadoInicial});

    const handleInputChange = (event) => {
   
        setUsuario({
            ...usuario,
            [event.target.name] : event.target.value
        })
    }
    

return(<section className="container">
<h1>Formulario Alta</h1>
    <form 
    onSubmit={(event) => {
        event.preventDefault()
        props.addUsuario(usuario)
        setUsuario(initialFormState)
      }}>
    <input type="text" name="category" placeholder="category" onChange={handleInputChange}/>
    <input type="text" name="price" placeholder="price" onChange={handleInputChange}/>
    <input type="text" name="nombre" placeholder="name" onChange={handleInputChange}/>


    <button type="submit" >Enviar</button>
    </form>
  
</section>)
}

export default FormularioAlta
