import React, { useState } from 'react'
import UserTable from './tablas/UserTable'
import AddUserForm from './forms/AddUserForm'
import EditUserForm from './forms/EditUserForm'
import SearchBar from './componentesC/SearchBar'


import "./index.css"

const Alta = (props) => {

 
    const [filterText, setFilterText] = useState(""); 
    const [inStockOnly, setInStockOnly] = useState(false);
    
  const products  = [
    {category: 'Sport Goods', price: '$49.99',  name: 'Football'},
    {category: 'Sporting Goods', price: '$9.99', name: 'Baseball'},
    {category: 'Sporting Goods', price: '$29.99',  name: 'Basketball'},
    {category: 'Electronics', price: '$99.99',  name: 'iPod Touch'},
    {category: 'Electronics', price: '$399.99',  name: 'iPhone 5'},
    {category: 'Electronics', price: '$199.99',  name: 'Nexus 7'}
  ];

  const initialFormState = { category: '', price: '', name: ''}

	
	const [ users, setUsers ] = useState(product)
	const [ currentUser, setCurrentUser ] = useState(initialFormState)
	const [ editing, setEditing ] = useState(false)

  const updateUser = (id, updatedUser) => {
    setEditing(false)
  
    setUsers(users.map((user) => (user.id === id ? updatedUser : user)))
  }

  const editRow = (user) => {
    setEditing(true)
  
    setCurrentUser({ category: user.category, price: user.price, name: user.name})
  }
  

  const addUser = (user) => {

 
    user.category = users.length + 1
    setUsers([...users, user])
    
  }

  const deleteUser = (category) => {
    setUsers(users.filter((user) => user.category !== category))
  }


  return (
    <div className="container-xxl">
      
      <div className="flex-row">
      <div className="flex-large">
  {editing ? (
    <div>
      
      <EditUserForm
        setEditing={setEditing}
        currentUser={currentUser}
        updateUser={updateUser}
      />
    </div>
  ) : (
    <div>
      <h2>Agregar Productos</h2>
      <AddUserForm addUser={addUser} />
    </div>
  )}
</div>
        <div className="flex-large">
          <h2>Tabla de los Heroes</h2>
      
    <div >
      <h1>Productos</h1>

      <SearchBar 
        filterText={filterText}
        inStockOnly={inStockOnly}  
        onFilterTextChange={text => setFilterText(text)}
        onInStockChange={stock => setInStockOnly(stock)}
      />

    
    
    </div>
          <br></br> <br></br>
          <UserTable 
          users={users} 
          editRow={editRow} 
          deleteUser={deleteUser} 
          products={props.products} 
          filterText={filterText}
          inStockOnly={inStockOnly} 
          
          />
        </div>
      </div>
    </div>
  )
}

export default Alta
